mapper.select:
SELECT * FROM tb_post p WHERE p.board_id = #{boardId} AND p.is_deleted = 0 AND p.status = 'ACTIVE'
AND (p.title LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY p.is_notice DESC, p.post_id DESC LIMIT #{limit} OFFSET #{offset}

mapper.insert:
INSERT INTO tb_post (
    board_id, title, content, author_id, is_secret, is_notice,
    status, created_by
) VALUES (
    #{post.boardId}, #{post.title}, #{post.content}, #{post.authorId},
    #{post.isSecret}, #{post.isNotice}, #{post.status}, #{post.createdBy}
)
